     * Initializes a new instance.
     *
     * @param params See {@link DefaultLayoutParams}.
     * @param ctx Execution context used for logging and tracing.
     */
    constructor(params: DefaultLayoutParams, ctx?: ContextTrait) {
        this.context = ctx
        const context = this.ctx().start('new Layout', ['View'])
        const {
            router,
            page,
            sideNavHeader,
            sideNavFooter,
            displayOptions,
            favoritesColumn,
            bookmarks$,
        } = params
        this.displayOptions = Object.assign(
            this.displayOptions,
            displayOptions ?? {},
        )
        if (this.displayOptions.forceNavDisplayMode) {
            this.displayModeNav$.next(this.displayOptions.forceNavDisplayMode)
        }
        if (this.displayOptions.forceTocDisplayMode) {
            this.displayModeToc$.next(this.displayOptions.forceTocDisplayMode)
        }
        this.connectedCallback = (e: RxHTMLElement<'div'>) => {
            this.plugResizer(e)
            router.setScrollableElement(e)
            e.ownSubscriptions(
                router.path$.subscribe(() => {
                    if (this.displayModeNav$.value === 'expanded') {
                        this.displayModeNav$.next('hidden')
                    }
                }),
            )
        }
        const viewInputs = {
            router,
            displayModeNav$: this.displayModeNav$,
            displayModeToc$: this.displayModeToc$,
            layoutOptions: this.displayOptions,
            bookmarks$,
            height$: this.height$,
        }

        const contentView = page
            ? page(viewInputs)
            : new PageView({ router: router }, context)
        const pageView = new WrapperPageView({
            content: contentView,
            displayOptions: this.displayOptions,
            displayModeNav$: this.displayModeNav$,
            displayModeToc$: this.displayModeToc$,
        })

        const favoritesView = favoritesColumn
            ? favoritesColumn(viewInputs)
            : new FavoritesView({
                  router,
                  bookmarks$: bookmarks$ ?? new BehaviorSubject([]),
                  displayMode$: this.displayModeNav$,
              })
        const navView = new NavigationWrapperView({
            router,
            displayOptions: this.displayOptions,
            bookmarks$,
            header: sideNavHeader?.(viewInputs),
            footer: sideNavFooter
                ? sideNavFooter(viewInputs)
                : new FooterView(),
        })
        const leftSideNav = {
            tag: 'div' as const,
            class: 'd-flex flex-grow-1',
            children: [
                bookmarks$
                    ? new StickyColumnContainer({
                          type: 'favorites',
                          content: favoritesView,
                          height$: this.height$,
                      })
                    : undefined,
                new StickyColumnContainer({
                    type: 'nav',
                    content: navView,
                    height$: this.height$,
                }),
            ],
        }
        const expandableLeftSideNav = new ExpandableNavColumn({
            favoritesView,
            navView,
            height$: this.height$,
            displayOptions: this.displayOptions,
            displayMode$: this.displayModeNav$,
        })
        const tocView = new TocWrapperView({
            router,
            displayMode$: this.displayModeToc$,
            displayOptions: this.displayOptions,
            content$: contentView.content$,
        })
        const rightSideNav = new StickyColumnContainer({
            type: 'toc',
            content: tocView,
            height$: this.height$,
        })
        const expandableRightSideNav = new ExpandableTocColumn({
            tocView,
            displayOptions: this.displayOptions,
            height$: this.height$,
            displayMode$: this.displayModeToc$,
        })
        const hSep = {
            tag: 'div' as const,
            class: 'flex-grow-1',
        }
        const switchMode = (
            mode$: Observable<DisplayMode>,
            pined: AnyVirtualDOM,
            expandable: AnyVirtualDOM,
        ) =>
            child$({
                source$: mode$.pipe(
                    map((mode) => (mode === 'pined' ? 'pined' : 'expandable')),
                    distinctUntilChanged(),
                ),
                vdomMap: (mode: 'pined' | 'expandable') =>
                    mode === 'pined' ? pined : expandable,
            })

        this.children = [
            {
                tag: 'div',
                class: 'w-100',
                children: [
                    {
                        tag: 'div',
                        class: 'd-flex w-100',
                        children: [
                            switchMode(
                                this.displayModeNav$,
                                leftSideNav,
                                expandableLeftSideNav,
                            ),
                            hSep,
                            pageView,
                            hSep,
                            switchMode(
                                this.displayModeToc$,
                                rightSideNav,
                                expandableRightSideNav,
                            ),
                        ],
                    },
                ],
            },
        ]
        context.exit()
    }

    ctx(ctx?: ContextTrait) {
        if (ctx) {
            return ctx
        }
        return this.context ?? new NoContext()
    }

    private plugResizer(thisElement: HTMLElement) {
        const switcher = (
            width: number,
            treshold: number,
            displayMode$: BehaviorSubject<DisplayMode>,
        ) => {
            if (width > treshold) {
                displayMode$.next('pined')
            }
            if (width <= treshold && displayMode$.value === 'pined') {
                displayMode$.next('hidden')
            }
        }
        const resizeObserver = new ResizeObserver((entries) => {
            const width = entries[0].contentRect.width
            const height = entries[0].contentRect.height
            this.height$.next(height)
            if (this.displayOptions.forceTocDisplayMode === undefined) {
                switcher(
                    width,
                    this.displayOptions.toggleTocWidth,
                    this.displayModeToc$,
                )
            }
            if (this.displayOptions.forceNavDisplayMode === undefined) {
                switcher(
                    width,
                    this.displayOptions.toggleNavWidth,
                    this.displayModeNav$,
                )
            }
        })
        if (thisElement.parentElement) {
            resizeObserver.observe(thisElement.parentElement)
        }
    }
}

constructor(params: LayoutWithCompanionParams, ctx?: ContextTrait) {
    this.context = ctx
    this.companionNodes$ = params.companionNodes$
    const context = this.ctx().start('new LayoutWithCompanion', ['View'])

    const mainView = new Layout({
        ...params,
        page: ({ router }) =>
            new PageView(
                {
                    router,
                },
                context,
            ),
    })
    const companionRouter = context.execute('setupRouters', (ctx) =>
        setupRouters(
            {
                router: params.router,
                companionNodes$: this.companionNodes$,
            },
            ctx,
        ),
    )

    const subs = [
        this.companionNodes$
            .pipe(
                filter((prefixes) => prefixes.length >= 1),
                take(1),
                switchMap((target) =>
                    from(companionRouter.navigateTo({ path: target[0] })),
                ),
            )
            .subscribe(),
    ]
    this.connectedCallback = (elem) => {
        elem.ownSubscriptions(...subs)
    }
    const companionWidth = params.displayOptions?.companionWidth ?? 40
    this.children = [
        {
            tag: 'div',
            class: 'h-100',
            style: attr$({
                source$: this.companionNodes$,
                vdomMap: (d) =>
                    d.length === 0
                        ? { width: '100%' }
                        : { width: `${String(100 - companionWidth)}%` },
            }),
            children: [mainView],
        },
        child$({
            source$: this.companionNodes$,
            vdomMap: (target) => {
                if (target.length === 0) {
                    return EmptyDiv
                }
                const pageView = new PageView(
                    {
                        router: companionRouter,
                    },
                    context,
                )
                const tocView = new TocWrapperView({
                    router: companionRouter,
                    displayMode$: new BehaviorSubject('hidden'),
                    displayOptions: mainView.displayOptions,
                    content$: pageView.content$,
                })
                const displayModeToc$ = new BehaviorSubject<DisplayMode>(
                    'hidden',
                )
                const expandableRightSideNav = new ExpandableTocColumn({
                    tocView,
                    displayOptions: mainView.displayOptions,
                    height$: mainView.height$,
                    displayMode$: displayModeToc$,
                })
                return {
                    tag: 'div',
                    class: 'bg-light h-100 ps-5 pe-2 overflow-y-auto overflow-x-hidden d-flex',
                    style: {
                        width: `${String(companionWidth)}%`,
                    },
                    children: [
                        new WrapperPageView({
                            content: pageView,
                            displayOptions: mainView.displayOptions,
                            displayModeToc$,
                        }),
                        expandableRightSideNav,
                    ],
                    connectedCallback: (e) => {
                        companionRouter.setScrollableElement(e)
                    },
                }
            },
        }),
    ]
    context.exit()
}

