export function popupModal({
    content,
    maxWidth,
    maxHeight,
}: {
    content: AnyView
    maxWidth?: string
    maxHeight?: string
}) {
    let element: HTMLElement | undefined = undefined
    maxWidth ??= '75%'
    maxHeight ??= '75%'
    const vdom: AnyVirtualDOM = {
        tag: 'div',
        style: {
            position: 'absolute',
            top: '0vh',
            left: '0vw',
            width: '100vw',
            height: '100vh',
            zIndex: 10,
            backdropFilter: 'blur(3px)',
        },
        class: 'd-flex flex-column justify-content-center',
        children: [
            {
                tag: 'div',
                class: `p-2 border rounded mx-auto d-flex flex-column justify-content-center overflow-auto`,
                children: [content],
                style: {
                    maxHeight: maxHeight,
                    maxWidth: maxWidth,
                },
                connectedCallback: (elem: HTMLElement) => {
                    const firstChild = elem.firstChild as HTMLElement
                    if (isSizeRelativeToParent(firstChild, 'width')) {
                        elem.style.width = maxWidth
                    }
                    if (isSizeRelativeToParent(firstChild, 'height')) {
                        elem.style.height = maxHeight
                    }
                },
                onclick: (ev) => {
                    ev.stopPropagation()
                },
            },
        ],
        connectedCallback: (elem: HTMLElement) => {
            // noinspection ReuseOfLocalVariableJS
            element = elem
        },
        onclick: () => {
            if (element) {
                element.remove()
            }
        },
    }
    document.body.appendChild(render(vdom))
}

