export class FooterView implements VirtualDOM<'div'> {
    public readonly tag = 'div'
    public readonly class =
        'w-75 mx-auto mkdocs-FooterView d-flex align-items-center flex-wrap justify-content-center border-top py-1'
    public readonly children: ChildrenLike

    constructor(params?: { sourceName: string; sourceUrl: string }) {
        const assetId = window.btoa(pkgJson.name)
        const baseIconPath = `/api/assets-gateway/webpm/resources/${assetId}/${pkgJson.version}/assets`
        const mkdocs: AnyVirtualDOM = {
            tag: 'div',
            class: 'd-flex align-items-center',
            children: [
                {
                    tag: 'div',
                    innerText: 'Made with',
                },
                {
                    tag: 'div',
                    class: 'mx-2',
                },
                {
                    tag: 'img',
                    src: `${baseIconPath}/mkdocs-ts.svg`,
                    width: 25,
                },
                {
                    tag: 'a',
                    class: 'mx-1',
                    innerText: 'mkdocs-ts',
                    target: '_blank',
                    href: '/apps/@mkdocs-ts/doc/latest',
                },
            ],
        }
        const sources = () => ({
            tag: 'div' as const,
            class: 'd-flex align-items-center px-1 border rounded mx-2 my-1',
            children: [
                {
                    tag: 'i' as const,
                    class: 'fas fa-code-branch',
                },
                {
                    tag: 'a' as const,
                    class: 'mx-1',
                    innerText: params?.sourceName,
                    target: '_blank',
                    href: params?.sourceUrl,
                },
            ],
        })
        this.children = [params ? sources() : undefined, mkdocs]
    }
}

constructor(params: DefaultLayoutParams, ctx?: ContextTrait) {
    this.context = ctx
    const context = this.ctx().start('new Layout', ['View'])
    const {
        router,
        page,
        topBanner,
        footer,
        displayOptions,
        bookmarks$,
        navFooter,
    } = params

    this.displayOptions = Object.assign(
        this.displayOptions,
        displayOptions ?? {},
    )

    this.connectedCallback = this.getConnectedCallback(router)

    const viewInputs = {
        router,
        layoutOptions: this.displayOptions,
        bookmarks$,
    }

    const navigationBoundingBox$ = new ReplaySubject<DOMRect>(1)
    const tocBoundingBox$ = new ReplaySubject<DOMRect>(1)

    const topBannerView = topBanner
        ? new TopBanner({
              router,
              navigationBoundingBox$,
              tocBoundingBox$,
              navDisplayMode$: this.displayModeNav$,
              spec: topBanner,
              pageScrollTop$: this.pageScrollTop$,
          })
        : new EmptyTopBanner()

    const footerView = new FooterWrapper({
        router,
        footer,
        withNav: navFooter,
    })

    const contentView = page
        ? page(viewInputs)
        : new PageView({ router: router }, context)

    const pageView = new WrapperPageView({
        content: contentView,
        displayOptions: this.displayOptions,
        displayModeNav$: this.displayModeNav$,
        displayModeToc$: this.displayModeToc$,
        minHeight$: LayoutObserver.minPageHeight$(
            this.appBoundingBox$,
            topBannerView.boundingBox$,
            footerView.boundingBox$,
        ),
    })
    this.layoutObserver = new LayoutObserver(
        {
            boxes: {
                pageBBox$: pageView.boundingBox$,
                topBannerBBox$: topBannerView.boundingBox$,
                footerBBox$: footerView.boundingBox$,
                appBBox$: this.appBoundingBox$,
                navBBox$: navigationBoundingBox$,
                tocBBox$: tocBoundingBox$,
            },
            pageScrollTop$: this.pageScrollTop$,
            displayModeNav$: this.displayModeNav$,
            displayModeToc$: this.displayModeToc$,
            displayModeOptions: this.displayOptions,
        },
        context,
    )

    const navView = new NavigationView({
        router: router,
        displayOptions: this.displayOptions,
        bookmarks$: bookmarks$,
    })
    const { pined: pinedNav, expandable: expandableNav } = this.sideView(
        navView,
        this.displayModeNav$,
        navigationBoundingBox$,
        ExpandableNavColumn,
    )

    const tocView = new TocWrapperView({
        router,
        displayMode$: this.displayModeToc$,
        displayOptions: this.displayOptions,
        content$: contentView.content$,
    })
    const { pined: pinedToc, expandable: expandableToc } = this.sideView(
        tocView,
        this.displayModeToc$,
        tocBoundingBox$,
        ExpandableTocColumn,
    )

    const hSep = {
        tag: 'div' as const,
        class: 'flex-grow-1',
    }
    this.children = [
        topBannerView,
        {
            tag: 'div',
            class: 'd-flex w-100',
            children: [
                this.layoutObserver.switchMode('nav', {
                    pined: pinedNav,
                    expandable: expandableNav,
                    removed: EmptyDiv,
                }),
                hSep,
                pageView,
                this.layoutObserver.switchMode('toc', {
                    pined: hSep,
                    expandable: EmptyDiv,
                    removed: hSep,
                }),
                this.layoutObserver.switchMode('toc', {
                    pined: pinedToc,
                    expandable: expandableToc,
                    removed: new EmptyToc(tocBoundingBox$),
                }),
            ],
        },
        footerView,
    ]
    context.exit()
}

export class Layout implements VirtualDOM<'div'> {
    /**
     * Component's class name for CSS query.
     */
    static readonly CssSelector = 'mkdocs-DefaultLayoutView'
    public readonly displayOptions: DisplayOptions = defaultDisplayOptions

    public readonly tag = 'div'
    public readonly children: ChildrenLike
    public readonly class = `${Layout.CssSelector} d-flex flex-column h-100 w-100 overflow-y-auto overflow-x-hidden`

    /**
     * The display mode regarding the navigation panel.
     */
    public readonly displayModeNav$ = new BehaviorSubject<DisplayMode>('pined')
    /**
     * The display mode regarding the table of content.
     */
    public readonly displayModeToc$ = new BehaviorSubject<DisplayMode>('pined')

    public readonly connectedCallback: (e: RxHTMLElement<'div'>) => void

    private readonly context?: ContextTrait

    public readonly pageScrollTop$ = new BehaviorSubject(0)

    public readonly layoutObserver: LayoutObserver

    private appBoundingBox$ = new ReplaySubject<DOMRect>(1)
    /**
     * Initializes a new instance.
     *
     * @param params See {@link DefaultLayoutParams}.
     * @param ctx Execution context used for logging and tracing.
     */
    constructor(params: DefaultLayoutParams, ctx?: ContextTrait) {
        this.context = ctx
        const context = this.ctx().start('new Layout', ['View'])
        const {
            router,
            page,
            topBanner,
            footer,
            displayOptions,
            bookmarks$,
            navFooter,
        } = params

        this.displayOptions = Object.assign(
            this.displayOptions,
            displayOptions ?? {},
        )

        this.connectedCallback = this.getConnectedCallback(router)

        const viewInputs = {
            router,
            layoutOptions: this.displayOptions,
            bookmarks$,
        }

        const navigationBoundingBox$ = new ReplaySubject<DOMRect>(1)
        const tocBoundingBox$ = new ReplaySubject<DOMRect>(1)

        const topBannerView = topBanner
            ? new TopBanner({
                  router,
                  navigationBoundingBox$,
                  tocBoundingBox$,
                  navDisplayMode$: this.displayModeNav$,
                  spec: topBanner,
                  pageScrollTop$: this.pageScrollTop$,
              })
            : new EmptyTopBanner()

        const footerView = new FooterWrapper({
            router,
            footer,
            withNav: navFooter,
        })

        const contentView = page
            ? page(viewInputs)
            : new PageView({ router: router }, context)

        const pageView = new WrapperPageView({
            content: contentView,
            displayOptions: this.displayOptions,
            displayModeNav$: this.displayModeNav$,
            displayModeToc$: this.displayModeToc$,
            minHeight$: LayoutObserver.minPageHeight$(
                this.appBoundingBox$,
                topBannerView.boundingBox$,
                footerView.boundingBox$,
            ),
        })
        this.layoutObserver = new LayoutObserver(
            {
                boxes: {
                    pageBBox$: pageView.boundingBox$,
                    topBannerBBox$: topBannerView.boundingBox$,
                    footerBBox$: footerView.boundingBox$,
                    appBBox$: this.appBoundingBox$,
                    navBBox$: navigationBoundingBox$,
                    tocBBox$: tocBoundingBox$,
                },
                pageScrollTop$: this.pageScrollTop$,
                displayModeNav$: this.displayModeNav$,
                displayModeToc$: this.displayModeToc$,
                displayModeOptions: this.displayOptions,
            },
            context,
        )

        const navView = new NavigationView({
            router: router,
            displayOptions: this.displayOptions,
            bookmarks$: bookmarks$,
        })
        const { pined: pinedNav, expandable: expandableNav } = this.sideView(
            navView,
            this.displayModeNav$,
            navigationBoundingBox$,
            ExpandableNavColumn,
        )

        const tocView = new TocWrapperView({
            router,
            displayMode$: this.displayModeToc$,
            displayOptions: this.displayOptions,
            content$: contentView.content$,
        })
        const { pined: pinedToc, expandable: expandableToc } = this.sideView(
            tocView,
            this.displayModeToc$,
            tocBoundingBox$,
            ExpandableTocColumn,
        )

        const hSep = {
            tag: 'div' as const,
            class: 'flex-grow-1',
        }
        this.children = [
            topBannerView,
            {
                tag: 'div',
                class: 'd-flex w-100',
                children: [
                    this.layoutObserver.switchMode('nav', {
                        pined: pinedNav,
                        expandable: expandableNav,
                        removed: EmptyDiv,
                    }),
                    hSep,
                    pageView,
                    this.layoutObserver.switchMode('toc', {
                        pined: hSep,
                        expandable: EmptyDiv,
                        removed: hSep,
                    }),
                    this.layoutObserver.switchMode('toc', {
                        pined: pinedToc,
                        expandable: expandableToc,
                        removed: new EmptyToc(tocBoundingBox$),
                    }),
                ],
            },
            footerView,
        ]
        context.exit()
    }

    ctx(ctx?: ContextTrait) {
        if (ctx) {
            return ctx
        }
        return this.context ?? new NoContext()
    }

    private sideView<T extends TocWrapperView | NavigationView>(
        content: T,
        displayMode$: BehaviorSubject<DisplayMode>,
        bbox$: Subject<BBox>,
        Expandable: new (
            p: ExpandableParams<T>,
        ) => T extends TocWrapperView
            ? ExpandableTocColumn
            : ExpandableNavColumn,
    ): {
        pined: AnyVirtualDOM
        expandable: AnyVirtualDOM
    } {
        const pined = StickyColumnContainer.wrap(
            content,
            this.layoutObserver,
            bbox$,
        )
        const expandable = new Expandable({
            content,
            layoutObserver: this.layoutObserver,
            displayOptions: this.displayOptions,
            displayMode$,
            boundingBox$: bbox$,
        })
        return {
            pined,
            expandable,
        }
    }

    private getConnectedCallback(router: Router) {
        return (e: RxHTMLElement<'div'>) => {
            const patchScrollTo = ({ top, left, behavior }: ScrollToOptions) =>
                this.layoutObserver.boxes$
                    .pipe(take(1))
                    .subscribe(({ topBanner }) => {
                        e.scrollTo({
                            left,
                            behavior,
                            top: top ? top - topBanner.height : 0,
                        })
                    })

            router.setScrollableElement(e, patchScrollTo)
            e.ownSubscriptions(
                router.path$.subscribe(() => {
                    if (this.displayModeNav$.value === 'expanded') {
                        this.displayModeNav$.next('hidden')
                    }
                }),
            )
            e.addEventListener('scroll', () => {
                this.pageScrollTop$.next(e.scrollTop)
            })
            plugBoundingBoxObserver(e, this.appBoundingBox$)
        }
    }
}

constructor(
    p: {
        boxes: {
            pageBBox$: BBox$
            topBannerBBox$: BBox$
            footerBBox$: BBox$
            appBBox$: BBox$
            navBBox$: BBox$
            tocBBox$: BBox$
        }
        pageScrollTop$: Observable<number>
        displayModeNav$: BehaviorSubject<DisplayMode>
        displayModeToc$: BehaviorSubject<DisplayMode>
        displayModeOptions: DisplayModeOptions
    },
    ctx?: ContextTrait,
) {
    const context = (ctx ?? new NoContext()).start('new LayoutObserver', [
        'View',
    ])

    this.displayModeNav$ = p.displayModeNav$
    this.displayModeToc$ = p.displayModeToc$
    this.displayModeOptions = p.displayModeOptions
    const round = (rect: BBox): BBox => ({
        width: Math.round(rect.width),
        height: Math.round(rect.height),
    })
    this.boxes$ = combineLatest([
        p.boxes.pageBBox$,
        p.boxes.topBannerBBox$,
        p.boxes.footerBBox$,
        p.boxes.appBBox$,
        p.boxes.navBBox$,
        p.boxes.tocBBox$,
    ]).pipe(
        map(([page, topBanner, footer, app, nav, toc]) => {
            return {
                page: round(page),
                topBanner: round(topBanner),
                footer: round(footer),
                app: round(app),
                nav: round(nav),
                toc: round(toc),
            }
        }),
        distinctUntilChanged(
            (prev, curr) => JSON.stringify(prev) === JSON.stringify(curr),
        ),
        tap((boxes) => {
            context.info('BBox update', boxes)
            this.displayModeSwitcher(boxes.app, context)
        }),
        shareReplay({ bufferSize: 1, refCount: true }),
    )
    this.pageVisible$ = combineLatest([this.boxes$, p.pageScrollTop$]).pipe(
        map(([{ page, topBanner, app }, scrollTop]) => {
            const remainingPageHeight = page.height - scrollTop
            const pageVisibleHeight =
                remainingPageHeight > app.height
                    ? app.height - topBanner.height
                    : remainingPageHeight
            return {
                top: topBanner.height,
                height: pageVisibleHeight,
            }
        }),
        shareReplay({ bufferSize: 1, refCount: true }),
    )

    if (this.displayModeOptions.forceNavDisplayMode) {
        this.displayModeNav$.next(
            this.displayModeOptions.forceNavDisplayMode,
        )
    }
    if (this.displayModeOptions.forceTocDisplayMode) {
        this.displayModeToc$.next(
            this.displayModeOptions.forceTocDisplayMode,
        )
    }
}

export class LayoutObserver {
    /**
     * Emits rounded layout bounding boxes whenever their dimensions change.
     * Shared replayed for efficient subscriptions.
     */
    public readonly boxes$: Observable<{
        page: BBox
        topBanner: BBox
        footer: BBox
        app: BBox
        nav: BBox
        toc: BBox
    }>
    /**
     * Represents the visible vertical area of the page content, taking banners and scroll position into account.
     */
    public readonly pageVisible$: Observable<{ top: number; height: number }>
    /**
     * Holds configuration for display mode toggling and force-mode settings.
     */
    public readonly displayModeOptions: DisplayModeOptions
    /**
     * Tracks the current display mode of the navigation panel. May be externally controlled or automatically updated.
     */
    public readonly displayModeNav$: BehaviorSubject<DisplayMode>
    /**
     * Tracks the current display mode of the TOC (Table of Contents) panel.
     */
    public readonly displayModeToc$: BehaviorSubject<DisplayMode>

    /**
     * Creates a new instance to watch layout bounding boxes and display mode settings.
     *
     * @param p
     * @param p.boxes Various bounding box observables.
     * @param p.pageScrollTop$ Stream of vertical scroll position for the page.
     * @param p.displayModeNav$ Reactive subject controlling navigation display mode.
     * @param p.displayModeToc$ Reactive subject controlling TOC display mode.
     * @param p.displayModeOptions Configuration for display mode behaviors .
     * @param ctx Logging and debugging context provider.
     */
    constructor(
        p: {
            boxes: {
                pageBBox$: BBox$
                topBannerBBox$: BBox$
                footerBBox$: BBox$
                appBBox$: BBox$
                navBBox$: BBox$
                tocBBox$: BBox$
            }
            pageScrollTop$: Observable<number>
            displayModeNav$: BehaviorSubject<DisplayMode>
            displayModeToc$: BehaviorSubject<DisplayMode>
            displayModeOptions: DisplayModeOptions
        },
        ctx?: ContextTrait,
    ) {
        const context = (ctx ?? new NoContext()).start('new LayoutObserver', [
            'View',
        ])

        this.displayModeNav$ = p.displayModeNav$
        this.displayModeToc$ = p.displayModeToc$
        this.displayModeOptions = p.displayModeOptions
        const round = (rect: BBox): BBox => ({
            width: Math.round(rect.width),
            height: Math.round(rect.height),
        })
        this.boxes$ = combineLatest([
            p.boxes.pageBBox$,
            p.boxes.topBannerBBox$,
            p.boxes.footerBBox$,
            p.boxes.appBBox$,
            p.boxes.navBBox$,
            p.boxes.tocBBox$,
        ]).pipe(
            map(([page, topBanner, footer, app, nav, toc]) => {
                return {
                    page: round(page),
                    topBanner: round(topBanner),
                    footer: round(footer),
                    app: round(app),
                    nav: round(nav),
                    toc: round(toc),
                }
            }),
            distinctUntilChanged(
                (prev, curr) => JSON.stringify(prev) === JSON.stringify(curr),
            ),
            tap((boxes) => {
                context.info('BBox update', boxes)
                this.displayModeSwitcher(boxes.app, context)
            }),
            shareReplay({ bufferSize: 1, refCount: true }),
        )
        this.pageVisible$ = combineLatest([this.boxes$, p.pageScrollTop$]).pipe(
            map(([{ page, topBanner, app }, scrollTop]) => {
                const remainingPageHeight = page.height - scrollTop
                const pageVisibleHeight =
                    remainingPageHeight > app.height
                        ? app.height - topBanner.height
                        : remainingPageHeight
                return {
                    top: topBanner.height,
                    height: pageVisibleHeight,
                }
            }),
            shareReplay({ bufferSize: 1, refCount: true }),
        )

        if (this.displayModeOptions.forceNavDisplayMode) {
            this.displayModeNav$.next(
                this.displayModeOptions.forceNavDisplayMode,
            )
        }
        if (this.displayModeOptions.forceTocDisplayMode) {
            this.displayModeToc$.next(
                this.displayModeOptions.forceTocDisplayMode,
            )
        }
    }
    private displayModeSwitcher(bbox: BBox, context: ContextTrait) {
        const switcher = (
            elem: 'nav' | 'toc',
            width: number,
            treshold: number,
            removed: number,
            displayMode$: BehaviorSubject<DisplayMode>,
        ) => {
            let nextValue: DisplayMode | undefined = undefined
            if (
                width <= treshold &&
                ['removed', 'pined'].includes(displayMode$.value)
            ) {
                nextValue = 'hidden'
            }
            if (width < removed) {
                nextValue = 'removed'
            }
            if (width > treshold) {
                nextValue = 'pined'
            }

            if (nextValue && displayMode$.value !== nextValue) {
                context.info(`Switch mode for ${elem}`, {
                    old: displayMode$.value,
                    new: nextValue,
                })
                displayMode$.next(nextValue)
            }
        }
        if (this.displayModeOptions.forceTocDisplayMode === undefined) {
            switcher(
                'toc',
                bbox.width,
                this.displayModeOptions.toggleTocWidth,
                this.displayModeOptions.toggleNavWidth,
                this.displayModeToc$,
            )
        }
        if (this.displayModeOptions.forceNavDisplayMode === undefined) {
            switcher(
                'nav',
                bbox.width,
                this.displayModeOptions.toggleNavWidth,
                0,
                this.displayModeNav$,
            )
        }
    }

    switchMode(
        source: 'nav' | 'toc',
        mapTo: {
            pined: AnyVirtualDOM
            expandable: AnyVirtualDOM
            removed: AnyVirtualDOM
        },
    ): RxChild {
        const source$ =
            source === 'nav' ? this.displayModeNav$ : this.displayModeToc$
        return child$({
            source$: source$.pipe(
                map((mode) => {
                    if (mode === 'removed') {
                        return 'removed'
                    }
                    return mode === 'pined' ? 'pined' : 'expandable'
                }),
                distinctUntilChanged(),
            ),
            vdomMap: (mode: 'pined' | 'expandable' | 'removed') => {
                if (mode === 'removed') {
                    return mapTo.removed
                }
                return mode === 'pined' ? mapTo.pined : mapTo.expandable
            },
        })
    }

    static minPageHeight$(app$: BBox$, topBanner$: BBox$, footer$: BBox$) {
        return combineLatest([app$, topBanner$, footer$]).pipe(
            map(
                ([appBB, topBB, footerBB]) =>
                    appBB.height - topBB.height - footerBB.height,
            ),
        )
    }
}

constructor(params: LayoutWithCompanionParams, ctx?: ContextTrait) {
    this.context = ctx
    this.companionNodes$ = params.companionNodes$
    const context = this.ctx().start('new LayoutWithCompanion', ['View'])

    const mainView = new Layout(
        {
            ...params,
            page: ({ router }) =>
                new PageView(
                    {
                        router,
                    },
                    context,
                ),
        },
        context,
    )
    const companionRouter = context.execute('setupRouters', (ctx) =>
        setupRouters(
            {
                router: params.router,
                companionNodes$: this.companionNodes$,
            },
            ctx,
        ),
    )

    const subs = [
        this.companionNodes$
            .pipe(
                filter((prefixes) => prefixes.length >= 1),
                take(1),
                switchMap((target) =>
                    from(companionRouter.navigateTo({ path: target[0] })),
                ),
            )
            .subscribe(),
    ]
    this.connectedCallback = (elem) => {
        elem.ownSubscriptions(...subs)
    }
    const companionWidth = params.displayOptions?.companionWidth ?? 40
    this.children = [
        {
            tag: 'div',
            class: 'h-100',
            style: attr$({
                source$: this.companionNodes$,
                vdomMap: (d) =>
                    d.length === 0
                        ? { width: '100%' }
                        : { width: `${String(100 - companionWidth)}%` },
            }),
            children: [mainView],
        },
        child$({
            source$: this.companionNodes$,
            vdomMap: (target) => {
                if (target.length === 0) {
                    return EmptyDiv
                }
                const pageView = new PageView(
                    {
                        router: companionRouter,
                    },
                    context,
                )
                const tocView = new TocWrapperView({
                    router: companionRouter,
                    displayMode$: new BehaviorSubject('hidden'),
                    displayOptions: mainView.displayOptions,
                    content$: pageView.content$,
                })
                const displayModeToc$ = new BehaviorSubject<DisplayMode>(
                    'hidden',
                )
                const tocBoundingBox$ = new ReplaySubject<DOMRect>(1)
                const expandableRightSideNav = new ExpandableTocColumn({
                    content: tocView,
                    displayOptions: mainView.displayOptions,
                    layoutObserver: mainView.layoutObserver,
                    displayMode$: displayModeToc$,
                    boundingBox$: tocBoundingBox$,
                })
                return {
                    tag: 'div',
                    class: 'bg-light h-100 ps-5 pe-2 overflow-y-auto overflow-x-hidden d-flex',
                    style: {
                        width: `${String(companionWidth)}%`,
                    },
                    children: [
                        new WrapperPageView({
                            content: pageView,
                            displayOptions: mainView.displayOptions,
                            displayModeToc$,
                            minHeight$: mainView.layoutObserver.boxes$.pipe(
                                map(({ app, topBanner, footer }) => {
                                    return (
                                        app.height -
                                        topBanner.height -
                                        footer.height
                                    )
                                }),
                            ),
                        }),
                        expandableRightSideNav,
                    ],
                    connectedCallback: (e) => {
                        companionRouter.setScrollableElement(e)
                    },
                }
            },
        }),
    ]
    context.exit()
}

