export function refreshResizeObservers(elem: HTMLElement) {
    Array.from(elem.querySelectorAll('.mkdocs-resize-observer')).forEach(
        (elem) => {
            const vdom = (elem as unknown as { vDom: ResizeObserverTrait }).vDom
            vdom.refreshView()
        },
    )
}
