export function notify({
    content,
    done$,
    duration,
    level,
}: {
    content: AnyView
    level: MdWidgets.NoteLevel | Observable<MdWidgets.NoteLevel>
    done$?: Observable<unknown>
    duration?: number
}) {
    let notif: HTMLElement
    const level$ = level instanceof Observable ? level : of(level)
    const containerId = 'notification-container'
    let container = document.getElementById(containerId)
    if (!container) {
        container = render({
            tag: 'div',
            id: containerId,
            class: 'overflow-auto',
            style: {
                fontSize: 'medium',
                position: 'fixed',
                maxHeight: 'calc(100vh - 10px)',
                maxWidth: '75vw',
                top: '0px',
                left: '50vw',
                transform: 'translateX(-50%)',
                // To be above the top banner (in 100...199)
                zIndex: 200,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            },
            connectedCallback: (elem) => (container = elem),
        })
        document.body.appendChild(container)
    }
    const closeBtn: AnyVirtualDOM = {
        tag: 'button',
        class: 'btn btn-sm btn-primary',
        style: { width: 'fit-content' },
        innerText: 'close',
        onclick: () => {
            notif.remove()
        },
    }
    const wrapped: AnyVirtualDOM = {
        tag: 'div',
        class: attr$({
            source$: level$,
            vdomMap: (level) => {
                return `mkdocs-bg-${level} border rounded p-3 my-1 w-100`
            },
        }),
        children: [content, closeBtn],
        connectedCallback: (elem) => (notif = elem),
    }
    container.appendChild(render(wrapped))
    if (done$) {
        done$.pipe(take(1)).subscribe(() => {
            notif.remove()
        })
    }
    if (duration) {
        setTimeout(() => {
            notif.remove()
        }, duration)
    }
}

