export class ModalNavChildrenView implements VirtualDOM<'div'> {
    public readonly router: Router
    public readonly node: NavNodeBase
    public readonly tag = 'div'
    public readonly class = 'mkdocs-ModalNavChildrenView'
    public readonly children: ChildrenLike
    constructor(params: { router: Router; node: NavNodeBase }) {
        Object.assign(this, params)
        const node = this.node.children
            ? this.node
            : this.router.explorerState.getParent(this.node.id)

        const source$ = this.router.explorerState.getChildren$(node)
        // Following call trigger children resolution if needed
        try {
            this.router.explorerState.getChildren(node)
        } catch (e) {
            this.children = []
            return
        }

        this.children = {
            policy: 'replace',
            source$: source$,
            vdomMap: (children: NavNodeBase[]) => {
                return children.map((child) => {
                    return {
                        tag: 'div',
                        class: 'border-bottom px-3 py-2',
                        children: [
                            new NavigationHeader({
                                node: child,
                                router: this.router,
                                withChildren: child.children && [
                                    {
                                        tag: 'div',
                                        class: 'fas fa-chevron-right',
                                    },
                                ],
                            }),
                        ],
                    }
                })
            },
        }
    }
}

export class ModalNavParentView implements VirtualDOM<'div'> {
    public readonly router: Router
    public readonly node: NavNodeBase
    public readonly tag = 'div'
    public readonly class =
        'mkdocs-ModalNavParentView w-100 py-3 border px-2 bg-light text-dark'
    public readonly style = {
        position: 'sticky' as const,
        top: '0px',
    }
    public readonly children: ChildrenLike
    public readonly displayModeToc$: Observable<DisplayMode>

    constructor(params: {
        router: Router
        node: NavNodeBase
        displayModeToc$: Observable<DisplayMode>
    }) {
        Object.assign(this, params)

        this.children = [
            {
                tag: 'i',
                class:
                    this.node.id === '/'
                        ? 'fas fa-arrow-left fv-text-disabled'
                        : 'fas fa-arrow-left fv-hover-text-focus fv-pointer',
                onclick: () => {
                    this.router.navigateToParent()
                },
            },
            new NavigationHeader(params),
            {
                source$: this.displayModeToc$.pipe(distinctUntilChanged()),
                vdomMap: (mode: DisplayMode) => {
                    return mode !== 'Minimized'
                        ? { tag: 'div' }
                        : new ModalTocView({ router: this.router })
                },
            },
        ]
    }
}

export class ModalNavigationView implements VirtualDOM<'div'> {
    public readonly router: Router

    public readonly tag = 'div'
    public readonly children: ChildrenLike
    public readonly class = 'mkdocs-ModalNavigationView'
    /**
     * Wether the modal is expanded or not.
     */
    public readonly expanded$ = new BehaviorSubject(false)
    public readonly displayModeToc$: Observable<DisplayMode>
    constructor(params: {
        router: Router
        displayModeToc$: Observable<DisplayMode>
    }) {
        Object.assign(this, params)

        this.children = [
            {
                source$: this.expanded$,
                vdomMap: (expanded) => {
                    return expanded
                        ? new ExpandedNavigationView({
                              router: this.router,
                              collapse: () => this.expanded$.next(false),
                              displayModeToc$: this.displayModeToc$,
                          })
                        : {
                              tag: 'div',
                              class: 'fas fa-bars p-1 fv-pointer fv-hover-text-focus',
                              onclick: () => this.expanded$.next(true),
                          }
                },
            },
        ]
    }
}

export class ModalTocView implements VirtualDOM<'div'> {
    public readonly tag = 'div'
    public readonly children: ChildrenLike
    public readonly router: Router
    public readonly class = 'mkdocs-ModalTocView'
    public readonly expanded$ = new BehaviorSubject(false)
    constructor(params: { router: Router }) {
        Object.assign(this, params)
        this.children = [
            {
                tag: 'div',
                class: 'd-flex align-items-center fv-hover-text-focus fv-pointer',
                onclick: () => this.expanded$.next(!this.expanded$.value),
                children: [
                    {
                        tag: 'div',
                        class: 'fas fa-list-ul',
                    },
                    {
                        tag: 'div',
                        class: 'pb-2 pt-1 mx-2',
                        innerText: 'Table of Content',
                    },
                    {
                        tag: 'div',
                        class: {
                            source$: this.expanded$,
                            vdomMap: (expanded) =>
                                expanded ? 'fa-chevron-up' : 'fa-chevron-down',
                            wrapper: (d) => `fas ${d} flex-grow-1 text-right`,
                        },
                    },
                ],
            },
            {
                tag: 'div',
                style: {
                    maxHeight: '25vh',
                },
                class: {
                    source$: this.expanded$,
                    vdomMap: (expanded) => (expanded ? 'd-block' : 'd-none'),
                },
                children: [new TocWrapperView({ router: this.router })],
            },
        ]
    }
}

export class NavigationView implements VirtualDOM<'div'> {
    public readonly router: Router

    public readonly tag = 'div'
    public readonly class = 'mkdocs-NavigationView h-100 w-100 overflow-auto'
    public readonly children: ChildrenLike

    constructor(params: { router: Router }) {
        Object.assign(this, params)

        this.children = [
            new ImmutableTree.View({
                state: this.router.explorerState,
                headerView: (explorerState, node) => {
                    return new NavigationHeader({
                        node,
                        router: this.router,
                        withChildren: node.children &&
                            node.id !== '/' && [
                                new HandlerView({
                                    node: node,
                                    expandedNodes$:
                                        this.router.explorerState
                                            .expandedNodes$,
                                }),
                            ],
                    })
                },
            }),
        ]
    }
}

export class PageView implements VirtualDOM<'div'> {
    public readonly router: Router
    public readonly tag = 'div'
    public readonly class = 'mkdocs-PageView w-100 mkdocs-ts-page'
    public readonly children: ChildrenLike

    public readonly connectedCallback: (html: RxHTMLElement<'div'>) => void

    constructor(params: { router: Router }) {
        Object.assign(this, params)
        this.children = [
            {
                source$: this.router.currentPage$,
                vdomMap: ({ html, sectionId }) => {
                    return {
                        tag: 'div',
                        children: [html as AnyVirtualDOM],
                        connectedCallback: (page) => {
                            this.router.scrollTo(sectionId)
                            this.router.setDisplayedPage({ page })
                            replaceCrossReferences(page, this.router)
                        },
                    }
                },
            },
        ]
    }
}

export class TOCView implements VirtualDOM<'div'> {
    public readonly router: Router
    public readonly html: HTMLElement
    public readonly tag = 'div'
    public readonly class = 'mkdocs-TOCView h-100'
    public readonly children: ChildrenLike
    public readonly style = {
        lineHeight: '1.5em',
    }

    public readonly indexFirstVisibleHeading$ = new BehaviorSubject<number>(0)
    public readonly connectedCallback: (elem: RxHTMLElement<'div'>) => void
    public readonly disconnectedCallback: (elem: RxHTMLElement<'div'>) => void

    constructor(params: {
        html: HTMLElement
        router: Router
        domConvertor?: (e: HTMLHeadingElement) => AnyVirtualDOM
    }) {
        Object.assign(this, params)
        const queryHeadings = supportedHeadingTags
            .reduce((acc, e) => `${acc},${e}`, '')
            .toLowerCase()
            .slice(1)
        const headingsArray = (): HTMLElement[] =>
            Array.from(this.html.querySelectorAll(queryHeadings))
        const headings$ = new BehaviorSubject<HTMLElement[]>(headingsArray())

        const allMutations$ = new Subject<MutationRecord[]>()

        const observer = new MutationObserver((mutationsList) => {
            allMutations$.next(mutationsList)
        })

        const headingsMutation$ = allMutations$.pipe(
            filter((mutationsList) => {
                const addedNodes: Node[] = mutationsList
                    .map((mut) =>
                        mut.type === 'childList'
                            ? [...mut.addedNodes, ...mut.removedNodes]
                            : [],
                    )
                    .flat()
                return (
                    addedNodes.find(
                        (node) =>
                            node['tagName'] && node['tagName'].startsWith('H'),
                    ) !== undefined
                )
            }),
            debounceTime(200),
        )

        this.connectedCallback = (elem) => {
            timer(1000, -1).subscribe(() => {
                headings$.next(headingsArray())
            })
            observer.observe(this.html, { childList: true, subtree: true })
            elem.ownSubscriptions(
                this.router.htmlUpdated$.subscribe(() => {
                    headings$.next(headingsArray())
                }),
            )
            elem.ownSubscriptions(
                timer(1000, -1)
                    .pipe(switchMap(() => headingsMutation$))
                    .subscribe(() => {
                        headings$.next(headingsArray())
                    }),
            )
        }
        this.disconnectedCallback = () => {
            observer.disconnect()
        }
        this.router.scrollableElement.onscroll = () => {
            this.getFirstVisible(headings$.value)
        }

        this.children = [
            {
                tag: 'ul',
                class: 'p-0 h-100 scrollbar-on-hover ',
                connectedCallback: (elem: RxHTMLElement<'ul'>) => {
                    const headings = [...elem.querySelectorAll('li')]
                    elem.ownSubscriptions(
                        this.indexFirstVisibleHeading$.subscribe((index) => {
                            const br = elem.getBoundingClientRect()
                            const offset = headings[index]?.['offsetTop'] || 0
                            elem.scrollTo({
                                top: offset + br.top - br.height / 4,
                                left: 0,
                                behavior: 'smooth',
                            })
                        }),
                    )
                },
                children: {
                    policy: 'replace',
                    source$: headings$,
                    vdomMap: (headingsArray: HTMLElement[]) => {
                        return headingsArray.map(
                            (heading: HTMLHeadingElement, index: number) => {
                                return new TocItemView({
                                    heading,
                                    index,
                                    indexFirstVisibleHeading$:
                                        this.indexFirstVisibleHeading$,
                                    router: this.router,
                                    domConvertor: params.domConvertor,
                                })
                            },
                        )
                    },
                },
            },
        ]
    }
    getFirstVisible(headings: HTMLElement[]) {
        for (let i = 0; i < headings.length; i++) {
            const rect = headings[i].getBoundingClientRect()

            if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
                this.indexFirstVisibleHeading$.next(i)
                return
            }
        }
    }
}

export class TopBannerView extends TopBannerBase {
    public readonly tag = 'div'
    public readonly class =
        'mkdocs-TopBannerView fv-text-primary fv-bg-background'

    constructor({
        name,
        displayModeNav$,
        displayModeToc$,
        router,
    }: {
        name: string
        displayModeNav$: Subject<DisplayMode>
        displayModeToc$: Subject<DisplayMode>
        router: Router
    }) {
        super({
            innerView: {
                tag: 'div',
                class: 'd-flex flex-column justify-content-center h-100',
                children: [
                    {
                        tag: 'div',
                        class: 'd-flex mx-auto align-items-center justify-content-center px-5',
                        children: [
                            {
                                source$: displayModeNav$.pipe(
                                    distinctUntilChanged(),
                                ),
                                vdomMap: (mode: DisplayMode) => {
                                    return mode === 'Full'
                                        ? { tag: 'div' }
                                        : new ModalNavigationView({
                                              router,
                                              displayModeToc$: displayModeToc$,
                                          })
                                },
                            },
                            {
                                tag: 'div',
                                class: 'mx-3',
                            },
                            {
                                tag: 'div',
                                class: 'd-flex align-items-center',
                                style: {
                                    width: '12.1em',
                                    padding: '1.2em 0 1.2em 0',
                                },
                                children: [
                                    {
                                        tag: 'a',
                                        class: 'fas fa-book-reader',
                                    },
                                    {
                                        tag: 'div',
                                        class: 'mx-3',
                                    },
                                    {
                                        tag: 'div',
                                        innerText: name,
                                        style: {
                                            fontWeight: 700,
                                            fontSize: '1.2em',
                                        },
                                    },
                                ],
                            },
                            {
                                tag: 'div',
                                style: {
                                    width: '800px',
                                },
                            },
                            {
                                tag: 'div',
                                style: {
                                    width: '12.1em',
                                    padding: '1.2em 0 1.2em 0',
                                },
                            },
                        ],
                    },
                ],
            },
        })
    }
}
