        const cellOptions = {
            ...defaultCellAttributes,
            ...(params.options?.defaultCellAttributes || {}),
        }
        this.children = [
            {
                source$,
                vdomMap: (src: string) => {
                    const vdom = parseMd({
                        src,
                        router: this.router,
                        ...(this.options?.markdown || {}),
                        views: {
                            ...(this.options?.markdown?.views || {}),
                            ...notebookViews({
                                state: this.state,
                                cellOptions,
                            }),
                        },
                    })
                    if (params?.options?.runAtStart) {
                        this.state.execute(this.state.ids.slice(-1)[0]).then()
                    }
                    return vdom
                },
            },
        ]
    }

