        lineNumbers,
        onExecute,
    }: {
        content: string
        language: 'markdown' | 'javascript' | 'python'
        readOnly: boolean
        lineNumbers: boolean
        onExecute: () => void
    }) {
        super({
            content,
            language,
            cmConfig: {
                lineNumbers,
                lineWrapping: false,
                indentUnit: 4,
                readOnly,
                extraKeys: {
                    'Ctrl-Enter': onExecute,
                },
            },
        })
    }
}

