export type CellCommonAttributes = {
    /**
     * Whether to display line numbers on cell.
     */
    lineNumbers?: boolean
    /**
     * Whether the cell is readonly.
     */
    readOnly?: boolean
}

export type JsCellAttributes = CellCommonAttributes & {
    /**
     * If the cell is reactive, Observables & Promises referenced are automatically resolved.
     * It uses a 'combineLatest' policy.
     */
    reactive: true
}

export type NotebookOptions = {
    /**
     * Whether to run all the cells of a notebook page when loaded.
     */
    runAtStart?: boolean
    /**
     * The default values for cell's attribute.
     */
    defaultCellAttributes?: CellCommonAttributes

    /**
     * Options for markdown parsing.
     */
    markdown?: MdParsingOptions
}

export type Scope = {
    /**
     * The `let` variables: keyed by their name and mapped to their values.
     */
    let: { [k: string]: unknown }
    /**
     * The `const` variables: keyed by their name and mapped to their values.
     */
    const: { [k: string]: unknown }

    /**
     * The exported globals of python runtime.
     */
    python: { [k: string]: unknown }
}

