code.implementation:
export async function executeJs$(
    inputs: ExecJsInput,
    ctx?: ContextTrait,
): Promise<Scope> {
    ctx = (ctx || new NoContext()).start('executeJs$', ['Exec'])

    const { src, error$, scope, invalidated$, output$ } = inputs
    const ast = parseProgram(src, inputs.cellId, error$)
    const declarations = extractGlobalDeclarations(ast)
    const undefs = extractUndefinedReferences(ast)

    ctx.info('Found undefined references in script', undefs)
    const select = (scopeConstLet: Record<string, unknown>) => {
        return Object.entries(scopeConstLet)
            .filter(
                ([k, v]) =>
                    undefs.includes(k) &&
                    (v instanceof Observable || v instanceof Promise),
            )
            .map(([k, v]) => {
                return [k, v instanceof Promise ? from(v) : v]
            }) as unknown as [string, Observable<unknown> | Promise<unknown>][]
    }
    const reactivesConst = select(scope.const)
    ctx.info('Undefined references bound to input scope observable retrieved', {
        reactivesConst,
    })
    const values$ = reactivesConst.map(([_, v]) => v)

    const extractValues = (values: unknown[]) => {
        return values.reduce((acc: Record<string, unknown>, e, i) => {
            const key = reactivesConst[i][0]
            return {
                ...acc,
                [key]: e,
            }
        }, {}) as unknown as Record<string, unknown>
    }
    const scope$ = combineLatest(values$).pipe(
        takeUntil(invalidated$),
        map(async (values) => {
            const reactScope = extractValues(values)
            const patchedScope = {
                ...scope,
                let: {}, // No mutable variables injected in reactive cells
                const: { ...scope.const, ...reactScope },
            }
            output$.next(undefined)
            return await executeJs(
                { ...inputs, scope: patchedScope, declarations },
                ctx,
            )
        }),
        switchMap((d) => from(d)),
        shareReplay({ bufferSize: 1, refCount: true }),
    )
    scope$.subscribe()

    const reactScopeConstOut = declarations.const.reduce((acc, k) => {
        return {
            ...acc,
            [k]: scope$.pipe(map((scopeOut) => scopeOut.const[k])),
        }
    }, {})

    ctx.info('Output scope observable created', {
        reactScopeConstOut,
    })
    ctx.exit()
    return {
        ...scope,
        const: { ...scope.const, ...reactScopeConstOut },
    }
}

export async function executeJsStatement(inputs: ExecJsInput) {
    return await executeJs$({
        ...inputs,
        src: `display(${inputs.src})`,
    })
}

export async function executePy(inputs: ExecPyInput, ctx?: ContextTrait) {
    ctx = (ctx ?? new NoContext()).start('executePy', ['Exec'])

    const {
        cellId,
        src,
        scope,
        output$,
        error$,
        displayFactory,
        invalidated$,
        pyNamespace,
    } = inputs

    const pyodide = scope.const.pyodide as Pyodide
    const sanitizedScope = fixScopeInvalidVar(scope, pyodide)
    ctx.info('Sanitized scope computed', { sanitizedScope })
    registerMknbModule(pyodide)

    const displayInOutput = (...element: HTMLElement[]) => {
        display(output$, displayFactory, ...element)
    }
    const pyCellModule = registerMknbCellModule({
        cellId,
        pyodide,
        displayInOutput,
        invalidated$,
        error$,
        scope: sanitizedScope,
        src,
    })

    const constKeys = Object.keys(sanitizedScope.const)
    const letKeys = Object.keys(sanitizedScope.let)
    Object.entries(scope.python).forEach(([k, v]) => {
        if (['string', 'number', 'boolean'].includes(typeof v)) {
            pyNamespace.set(k, v)
        }
    })
    const localsAssignments = Object.keys(scope.python).reduce(
        (acc, e) => `${acc}\n${e}=globals()['${e}']`,
        '',
    )
    const importsLet =
        letKeys.length > 0
            ? `from ${pyCellModule} import ${String(letKeys)}`
            : ''
    const importsConst =
        constKeys.length > 0
            ? `from ${pyCellModule} import ${String(constKeys)}`
            : ''

    const pyVarsCount = Object.keys(scope.python).length

    const srcPatched = `
return async (scope, { pyNamespace }) => {

    const pyScope = await scope.const.pyodide.runPythonAsync(\`
        from mknb import pyFctWrapperJs, HandledException
        import traceback

        try:
            def run():
                ${indent(localsAssignments, '                ')}
                from ${pyCellModule} import invalidated_, display
                ${importsLet}
                ${importsConst}

                initial_vars = { k: v for k, v in locals().items() }

                ${indent(src, '                ')}

                final_vars = { k: v for k, v in locals().items() }
                final_keys = final_vars.keys()
                new_vars = [k for k in final_keys if k not in initial_vars or initial_vars[k] is not final_vars[k] ]   
                new_scope = { k: final_vars[k] for k in new_vars}
                return new_scope, new_vars

            new_scope, new_vars = run()
            for k, v in new_scope.items():
                globals()[k] = v

        except Exception as e:
            from ${pyCellModule} import emit_error
            tb = traceback.extract_tb(e.__traceback__)
            for entry in reversed(tb):
                if entry.filename == "<exec>":
                    error_line = entry.lineno
                    break
            else:
                error_line = None

            tb_list = traceback.format_exception(type(e), e, e.__traceback__)
            emit_error({ 
                "kind": "Runtime", 
                "message": str(e), 
                "stackTrace": tb_list, 
                "lineNumber": error_line - 13 - ${String(pyVarsCount)}
            })
            raise HandledException("Handled error: " + str(e), original_exception=e)

        new_scope, new_vars
    \`, { globals: pyNamespace } )

    return pyScope.toJs({dict_converter:  Object.fromEntries})
}`
    try {
        ctx.info('Wrapped src defined', { wrappedSrc: srcPatched })

        // eslint-disable-next-line @typescript-eslint/no-implied-eval,@typescript-eslint/no-unsafe-call
        const [pyScopeOut, newGlobals] = (await new Function(srcPatched)()(
            scope,
            { pyNamespace },
        )) as [Record<string, unknown>, string[]]
        const scopeOut = {
            let: scope.let,
            const: scope.const,
            python: { ...scope.python, ...pyScopeOut },
        }
        ctx.info('Py cell execution done', {
            src,
            scopeIn: scope,
            scopeOut,
            newGlobals,
        })
        invalidated$.pipe(take(1)).subscribe(() => {
            ctx.info('Destroy py proxies', { scopeOut, newGlobals })
            Object.entries(scopeOut.python)
                .filter(([k]) => {
                    return newGlobals.includes(k)
                })
                .filter(([, v]) => isDestroyable(v))
                .forEach(([k, v]: [k: string, v: { destroy: () => void }]) => {
                    ctx.info(`Destroy py proxy ${k}`)
                    v.destroy()
                })
        })
        ctx.exit()
        return scopeOut
    } catch (e) {
        if (isInstanceofPyException(e)) {
            const error: ExecCellError = {
                cellId,
                kind: 'AST',
                src: src.split('\n'),
                message: e.message.split('\n').slice(-2)[0],
                stackTrace: e.stack.split('\n'),
                scopeIn: scope,
            }
            error$.next(error)
        }
        ctx.error('Failed to execute cell', e)
        ctx.exit()
        throw e
    }
}

