export function htmlView(
    element: HTMLElement | AnyVirtualDOM | RxChild,
): AnyVirtualDOM

code.implementation:
export function htmlView(
    element: HTMLElement | AnyVirtualDOM | RxChild,
): AnyVirtualDOM {
    if (element instanceof HTMLElement) {
        return {
            tag: 'div',
            children: [element],
        }
    }
    function isRxChild(e: unknown): e is RxChild {
        if (typeof e !== 'object' || e === null) {
            return false
        }
        return 'source$' in e && 'vdomMap' in e
    }
    if (isRxChild(element)) {
        return { tag: 'div', children: [element] }
    }
    return element
}

export function parseStyle(styleString: string): CSSAttribute {
    if (!styleString) {
        return {}
    }
    const stylePairs = styleString.split(';')
    const styleObject = {}

    stylePairs.forEach((pair) => {
        const [property, value] = pair.split(':').map((s) => s.trim())
        if (property && value) {
            styleObject[property] = value
        }
    })

    return styleObject
}

export function rawView(element: unknown): AnyVirtualDOM {
    type PrimitiveType = 'string' | 'number' | 'boolean'
    if (['string', 'number', 'boolean'].includes(typeof element)) {
        return {
            tag: 'div',
            innerText: element as PrimitiveType,
        }
    }
    const state = new ObjectJs.State({ title: '', data: element })
    return {
        tag: 'div' as const,
        class: 'cm-s-default',
        children: [new ObjectJs.View({ state })],
    }
}

export function registerMknbCellModule({
    cellId,
    pyodide,
    displayInOutput,
    invalidated$,
    error$,
    scope,
    src,
}: {
    cellId: string
    pyodide: Pyodide
    displayInOutput: (...element: HTMLElement[]) => void
    invalidated$: Observable<unknown>
    error$: Subject<ExecCellError | undefined>
    scope: Scope
    src: string
}) {
    pyodide.runPython(`
import sys
if 'mknb_cell_${cellId}' in sys.modules:
    del sys.modules['mknb_cell_${cellId}']    
    `)
    pyodide.registerJsModule(`mknb_cell_${cellId}`, {
        display: (...element: HTMLElement[]) => {
            displayInOutput(...element)
        },
        emit_error: (e: PyodideProxy<Omit<ExecCellError, 'src'>>) => {
            const converted = e.toJs({ dict_converter: Object.fromEntries })
            error$.next({ ...converted, src: src.split('\n'), scopeIn: scope })
        },
        invalidated_: invalidated$,
        ...scope.let,
        ...scope.const,
    })
    return `mknb_cell_${cellId}`
}
